INSERT INTO `pages` (`id`,`subject_id`,`menu_name`,`position`,`visible`,`content`) VALUES 
 (16,2,'HTML Tags',2,1,'<p style=\"font-size: 10pt; font-family: Arial, Verdana; font-style: normal; font-variant: normal; font-weight: normal; line-height: normal;\"></p><p style=\"font-family: Arial, Verdana; font-style: normal; font-variant: normal; font-weight: normal; line-height: normal; text-align: justify;\"><p><b><span style=\"font-family: Arial, sans-serif;\">Paragraphs<u1:p></u1:p></span></b><span style=\"font-family: Arial, sans-serif;\"><o:p></o:p></span></p>\r\n\r\n<p><b><span \"=\"\"><span style=\"font-family: Arial, sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"apple-converted-space\">&nbsp;</span></span></span></b><span \"=\"\"><span style=\"font-family: Arial, sans-serif;\">Paragraphs are defined\r\nwith the<o:p></o:p></span></span></p>\r\n\r\n\r\n\r\n<p style=\"text-align: start;\"><b><span style=\"font-family: Arial, sans-serif;\">tag.</span><u1:p></u1:p></b><span style=\"font-family: Arial, sans-serif;\"><o:p></o:p></span></p>\r\n\r\n<p><span style=\"font-family: Arial, sans-serif; background-color: black; background-position: initial initial; background-repeat: initial initial;\">This is a paragraph.</span><span style=\"font-family: Arial, sans-serif;\"><o:p></o:p></span></p>\r\n\r\n<p style=\"text-align: start;\"><span style=\"font-family: Arial, sans-serif; background-color: black; background-position: initial initial; background-repeat: initial initial;\">This is another paragraph.</span><span style=\"font-family: Arial, sans-serif;\"><o:p></o:p></span></p>\r\n\r\n<p style=\"text-align: start;\"><span style=\"font-family: Arial, sans-serif;\"><span \"=\"\">HTML automatically adds an extra blank line before and\r\nafter a paragraph.<u1:p></u1:p></span><o:p></o:p></span></p>\r\n\r\n<p style=\"text-align: start;\"><b><span \"=\"\"><span style=\"font-family: Arial, sans-serif;\">&nbsp;Line Breaks<u1:p></u1:p></span></span></b><span style=\"font-family: Arial, sans-serif;\"><o:p></o:p></span></p>\r\n\r\n<p style=\"text-align: start;\"><span \";=\"\" \"times=\"\" new=\"\" \";mso-ansi-language:en-us;mso-fareast-language:=\"\" \"=\"\"><span style=\"font-family: Arial, sans-serif;\">This<span class=\"apple-converted-space\">&nbsp;</span><b><br>\r\ntag<span class=\"apple-converted-space\">&nbsp;</span></b>&nbsp;is used when you\r\nwant to end&nbsp; a line, but do not want to start a new paragraph. The<span class=\"apple-converted-space\">&nbsp;</span><br>\r\ntag forces a line to break wherever you place it.</span><o:p></o:p></span></p>\r\n\r\n<p style=\"text-align: start;\"><span style=\"font-family: Arial, sans-serif;\">This<span class=\"apple-converted-space\"><b>&nbsp;</b></span><b><br>\r\n</b>is a para<b><br>\r\n</b>graph with line breaks.<o:p></o:p></span></p>\r\n\r\n<p style=\"text-align: start;\"><span style=\"font-family: Arial, sans-serif;\"><u1:p></u1:p><span \"=\"\">The<span class=\"apple-converted-space\">&nbsp;</span><b><br>\r\ntag<span class=\"apple-converted-space\">&nbsp;</span></b>is an empty tag. It has\r\nno closing tag.<u1:p></u1:p></span><o:p></o:p></span></p>\r\n\r\n<h2 style=\"text-align: start;\"><span \"=\"\"><span style=\"font-size: 12pt; font-family: Arial, sans-serif; font-weight: normal;\">Headings</span><o:p></o:p></span></h2>\r\n\r\n<u1:p></u1:p>\r\n\r\n<p style=\"text-align: start;\"><span \"=\"\"><span style=\"font-family: Arial, sans-serif;\">Headings are defined with\r\nthe</span> <b>to tags.</b><o:p></o:p></span></p>\r\n\r\n<h1><span style=\"font-size: 12pt; line-height: 115%; font-family: Arial, sans-serif; font-weight: normal;\">defines the largest heading.<o:p></o:p></span></h1>\r\n\r\n<h6 style=\"text-align: start;\"><b><span style=\"font-size: 12pt; line-height: 115%; font-family: Arial, sans-serif;\">defines\r\nthe smallest heading.<o:p></o:p></span></b></h6>\r\n\r\n<h1><span style=\"font-size:12.0pt;line-height:115%;font-family:&quot;Arial&quot;,&quot;sans-serif&quot;;\r\ncolor:white;background:#333333;font-weight:normal\">This is a heading</span><span style=\"font-size: 12pt; line-height: 115%; font-family: Arial, sans-serif;\"><o:p></o:p></span></h1>\r\n\r\n<h2><span style=\"font-size: 12pt; font-family: Arial, sans-serif; background-color: rgb(51, 51, 51); background-position: initial initial; background-repeat: initial initial;\"><u1:p></u1:p>This is a heading<o:p></o:p></span></h2>\r\n\r\n<h3 style=\"text-align: start;\"><span style=\"font-size: 12pt; line-height: 115%; font-family: Arial, sans-serif;\">This is a\r\nheading<o:p></o:p></span></h3>\r\n\r\n<h4><span style=\"font-size:12.0pt;line-height:115%;font-family:&quot;Arial&quot;,&quot;sans-serif&quot;;\r\ncolor:white;background:#333333;font-weight:normal\"><u1:p></u1:p>This is a\r\nheading</span><span style=\"font-size: 12pt; line-height: 115%; font-family: Arial, sans-serif;\"><o:p></o:p></span></h4>\r\n\r\n<h5><b><span style=\"font-size:12.0pt;line-height:115%;font-family:&quot;Arial&quot;,&quot;sans-serif&quot;;\r\ncolor:white;background:#333333\"><u1:p></u1:p>This is a heading</span></b><span style=\"font-size: 12pt; line-height: 115%; font-family: Arial, sans-serif;\"><o:p></o:p></span></h5>\r\n\r\n<h6 style=\"text-align: start;\"><b><span style=\"font-size:\r\n12.0pt;line-height:115%;font-family:&quot;Arial&quot;,&quot;sans-serif&quot;;color:white;\r\nbackground:#333333\"><u1:p></u1:p>This is a heading</span></b><span style=\"font-size: 12pt; line-height: 115%; font-family: Arial, sans-serif;\"><o:p></o:p></span></h6>\r\n\r\n<p style=\"text-align: start;\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;;\r\ncolor:white;background:#333333\"><span \"=\"\">HTML automatically adds an extra\r\nblank line before and after a heading.</span></span><span style=\"font-family: Arial, sans-serif;\"><o:p></o:p></span></p>\r\n\r\n<u1:p></u1:p>\r\n\r\n<h2 style=\"text-align: start;\"><span \"=\"\"><span style=\"font-size:12.0pt;font-family:&quot;Arial&quot;,&quot;sans-serif&quot;;color:white;\r\nbackground:#333333;font-weight:normal\">Don\'t Forget the Closing Tag</span></span><span style=\"font-size: 12pt; font-family: Arial, sans-serif; font-weight: normal;\"><o:p></o:p></span></h2>\r\n\r\n<u1:p></u1:p>\r\n\r\n<p style=\"text-align: start;\"><span \"=\"\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;;color:white;background:#333333\">You\r\nmight have noticed that paragraphs can be written without end tags</span></span><span style=\"font-family: Arial, sans-serif;\"><o:p></o:p></span></p>\r\n\r\n<p class=\"MsoNormal\"><span style=\"font-size:12.0pt;line-height:115%;font-family:\r\n&quot;Arial&quot;,&quot;sans-serif&quot;;color:white;background:#333333\">:</span><span style=\"font-size:12.0pt;line-height:115%;font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\"><o:p></o:p></span></p>\r\n\r\n<u1:p></u1:p>\r\n\r\n<p style=\"text-align: start;\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;;\r\ncolor:white;background:black\">This is a paragraph</span><span style=\"font-family: Arial, sans-serif; background-color: black; background-position: initial initial; background-repeat: initial initial;\"><o:p></o:p></span></p>\r\n\r\n<u1:p></u1:p><span style=\"text-align: start;\">\r\n\r\n<p><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;;color:white;background:black\">This\r\nis another paragraph</span><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;;\r\ncolor:white\"><o:p></o:p></span></p>\r\n\r\n<p><span \"=\"\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;;color:white;\r\nbackground:black\">The example above will work in most browsers, but don\'t rely\r\non it. Future version of HTML will not allow you to skip ANY end tags.<u1:p></u1:p></span></span><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;;color:white\"><o:p></o:p></span></p>\r\n\r\n<p><span \"=\"\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;;color:white;\r\nbackground:black\">Closing all HTML elements with an end tag is a future-proof\r\nway of writing HTML. It also makes the code easier to understand (read and\r\nbrowse) when you mark both where an element starts and where it ends.<u1:p></u1:p></span></span><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;;color:white\"><o:p></o:p></span></p>\r\n\r\n<h2><span \";=\"\" \"=\"\"><span style=\"font-size:12.0pt;font-family:&quot;Arial&quot;,&quot;sans-serif&quot;;\r\ncolor:white;background:black\">Comments in HTML<u1:p></u1:p></span></span><span style=\"font-size:12.0pt;font-family:&quot;Arial&quot;,&quot;sans-serif&quot;;color:white\"><o:p></o:p></span></h2>\r\n\r\n<p><span \"=\"\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;;color:white;\r\nbackground:black\">The comment tag is used to insert a comment in the HTML\r\nsource code. A comment will be ignored by the browser. You can use comments to\r\nexplain your code, which can help you when you edit the source code at a later\r\ndate.<u1:p></u1:p></span></span><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;;\r\ncolor:white\"><o:p></o:p></span></p>\r\n\r\n<p><span \"=\"\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;;color:white;\r\nbackground:black\">Note that you need an exclamation point after the opening\r\nbracket, but not before the closing bracket.<u1:p></u1:p></span></span><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;;color:white\"><o:p></o:p></span></p>\r\n\r\n<p class=\"MsoNormal\"><b><span \"=\"\"><span style=\"font-size:12.0pt;line-height:115%;font-family:&quot;Arial&quot;,&quot;sans-serif&quot;;\r\ncolor:white;background:black\">Center aligned heading<u1:p></u1:p></span></span></b><span style=\"font-size:12.0pt;line-height:115%;font-family:&quot;Arial&quot;,&quot;sans-serif&quot;;\r\ncolor:white\"><o:p></o:p></span></p>\r\n\r\n<p class=\"MsoNormal\"><b><span \"=\"\"><span style=\"font-size:12.0pt;line-height:115%;font-family:&quot;Arial&quot;,&quot;sans-serif&quot;;\r\ncolor:white;background:black\">&nbsp; &nbsp;</span></span></b><span style=\"font-size:12.0pt;line-height:115%;font-family:&quot;Arial&quot;,&quot;sans-serif&quot;;\r\ncolor:white;background:black\">This causes the heading to be displayed at the\r\ncenter of the browser window.</span><span style=\"font-size:12.0pt;line-height:\r\n115%;font-family:&quot;Arial&quot;,&quot;sans-serif&quot;;color:white\"><o:p></o:p></span></p>\r\n\r\n<table class=\"MsoNormalTable\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" style=\"border-collapse:collapse;mso-yfti-tbllook:1184;mso-padding-alt:0in 0in 0in 0in\">\r\n <tbody><tr>\r\n  <td width=\"295\" valign=\"top\" style=\"width:221.4pt;border:solid windowtext 1.0pt;\r\n  padding:0in 5.4pt 0in 5.4pt\">\r\n  <p class=\"MsoNormal\"><span style=\"font-size:12.0pt;line-height:115%;font-family:&quot;Arial&quot;,&quot;sans-serif&quot;;\r\n  background:black\">h1 align=center&gt;<u1:p></u1:p></span><span style=\"font-size:12.0pt;line-height:115%;font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\"><o:p></o:p></span></p>\r\n  <p class=\"MsoNormal\"><span style=\"font-size:12.0pt;line-height:115%;font-family:&quot;Arial&quot;,&quot;sans-serif&quot;;\r\n  background:black\">This is a heading 1<u1:p></u1:p></span><span style=\"font-size:12.0pt;line-height:115%;font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\"><o:p></o:p></span></p>\r\n  </td>\r\n  <td width=\"295\" valign=\"top\" style=\"width:221.4pt;border:solid windowtext 1.0pt;\r\n  border-left:none;padding:0in 5.4pt 0in 5.4pt\">\r\n  <p class=\"MsoNormal\"><b><span style=\"font-size:12.0pt;line-height:115%;font-family:&quot;Arial&quot;,&quot;sans-serif&quot;;\r\n  background:black\">This is a heading 1</span><u1:p></u1:p></b><span style=\"font-size:12.0pt;line-height:115%;font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\"><o:p></o:p></span></p>\r\n  </td>\r\n </tr>\r\n</tbody></table>\r\n\r\n<p class=\"MsoNormal\"><span style=\"font-size:12.0pt;